import React from 'react'
import { animateScroll as scroll } from 'react-scroll'
import { TiArrowUpThick } from "react-icons/ti";

export default function Transition({image, dark, home}) {

    const scrollToTop = () => {
        scroll.scrollToTop()
    }

    return (
        <>
            <div className='image'>
                <img src={image} alt='Transitional Image' />
                <h5 onClick={scrollToTop} className={dark ? 'arrow-up-icon-dark' : 'arrow-up-icon'}><TiArrowUpThick /></h5>
            </div>
            <style jsx>{`
                .image {
                    width: 100%;
                }
                img {
                    width: 100%;
                }
                .arrow-up-icon-dark {
                    color: #e5e7e6;
                    margin-left: 49%;
                    margin-top: 10%;
                    font-size: 2.5rem;
                }
                .arrow-up-icon {
                    color: #000c1f;
                    margin-left: 49%;
                    margin-top: 10%;
                    font-size: 2.5rem;
                }
      `}</style>
        </>
    )
}
